effect.shader:

    struct VS_IN
{
	float4 pos : POSITION;
};

struct VS_OUT
{
	float4 pos : SV_POSITION;
	float2 tex : TEXCOORD;
};

VS_OUT QuadVS(VS_IN vsIn)
{
	VS_OUT vsOut;

	vsOut.pos = vsIn.pos;
	vsOut.tex = float2(vsIn.pos.x * 0.5f + 0.5f, vsIn.pos.y * -0.5f + 0.5f);

	return vsOut;
}

float4 MergePS(VS_OUT vsOut) : SV_TARGET
{
	return rs0.Sample(sam, vsOut.tex) + rs1.Sample(sam, vsOut.tex);
}
