struct PostProcessVSIn
{
  float2 pos : POSITION;
  float2 uv : TEXCOORD;
};

struct PostProcessVSOut
{
	float4 pos : SV_POSITION;
	float2 tex : TEXCOORD;
};

PostProcessVSOut PostProcessVS(
    PostProcessVSIn vsIn)
{
	  PostProcessVSOut vsOut;

    float4 posMap[4] = 
    {
      float4(-1.0f,  1.0f, 0.0f, 1.0f),
      float4( 1.0f,  1.0f, 0.0f, 1.0f),
      float4(-1.0f, -1.0f, 0.0f, 1.0f),
      float4( 1.0f, -1.0f, 0.0f, 1.0f)
    };

    float2 uvMap[4] = (float2 [4])ppUV;

	  //vsOut.pos = posMap[vertexID];
    //vsOut.tex = uvMap[vertexID];

    vsOut.pos = float4(vsIn.pos, 0.0f, 1.0f);
    vsOut.tex = vsIn.uv;
	  //vsOut.tex = float2(vsIn.pos.x * 0.5f + 0.5f, vsIn.pos.y * -0.5f + 0.5f);

	  return vsOut;
}
