effect.shader:


float4 TransformPS(PostProcessVSOut vsOut) : SV_TARGET
{
    float4 colorSample = srcTex.SampleLevel(samplerLinearClamp, float2(vsOut.tex), 0);

    float4 outColor = 0.0f;

#ifdef COLOR_WRITE_R
    outColor.r = colorSample.r;
#endif

#ifdef COLOR_WRITE_G
    outColor.g = colorSample.g;
#endif

#ifdef COLOR_WRITE_B
    outColor.b = colorSample.b;
#endif

#ifdef COLOR_WRITE_A
    outColor.a = colorSample.a;
#endif

    return outColor;
}
